INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (21, '기본 포함 품목', '파워트레인/성능', '8단 자동변속기', '전달 효율 증대로 전 엔진 동급 최고의 연비를 구현함은 물론, 최적의 변속 성능으로 드라이빙 감성까지 향상시켜줍니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/power/transmission.jpg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (22, '기본 포함 품목', '파워트레인/성능', 'ISG 시스템', '신호 대기 상황이거나 정차 중일 때 차의 엔진을 일시 정지하여 연비를 향상시키고, 배출가스 발생을 억제하는 시스템입니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/power/ISG.jpg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (23, '기본 포함 품목', '파워트레인/성능', '통합주행모드', 'COMFORT, ECO, SPORT, SMART 4가지 드라이브 모드를 지원하여 운전자의 니즈에 따른 다양한 주행 모드를 선택할 수 있습니다. 각 주행모드의 상태는 클러스터와 AVN을 통해 확인 가능합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/power/driving_mode.jpg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (24, '기본 포함 품목', '파워트레인/성능', '랙구동형 전동식 파워스티어링(R-MD', '조향 시 운전자의 힘을 유압대신 모터가 바퀴로 전달하는 기술로 모터가 차량 하체에 장착되어 타이어를 제어합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/power/power_steering.jpg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (25, '기본 포함 품목', '파워트레인/성능', '전자식 변속버튼', '전자식 변속 버튼을 적용하여 조작 편의성을 높이는 동시에 하이테크적인 이미지를 연출했습니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/power/shift_button.jpg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (26, '기본 포함 품목', '지능형 안전기술', '하이빔 보조', '마주오는 차량 또는 앞 차량의 램프 및 주변 밝기 상태를 감지하여 전조등을 자동으로 상향 또는 하향으로 전환합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/ai/hba.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (27, '기본 포함 품목', '지능형 안전기술', '진동 경고 스티어링 휠', '전방 차량과의 충돌/차량의 차선 이탈/후측방 충돌 위험 시, 운전자 주의 경고 시 스티어링에 진동을 주어 경고합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/ai/vibsteeringwheel.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (28, '기본 포함 품목', '지능형 안전기술', '스마트 크루즈 컨트롤(스탑앤고 기능 ', '전방의 차량을 감지하여 스스로 가속과 감속을 하며 차량의 속도를 일정하게 유지 시켜주고, 차량 정체로 앞차 정차 시 정지하고 전방 차량 출발 시 다시 거리를 유지하며 주행하는 기능입니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (29, '기본 포함 품목', '지능형 안전기술', '내비게이션 기반 스마트 크루즈 컨트롤', '스마트 크루즈 작동 중 고속도로/도시고속도로/자동차전용 도로 내 안전구간 진입 시, 감속 제어를 통해 주행 편의 제공합니다. 또한 곡선 구간 진입 시, 감속 제어를 통해 주행 편의를 제공합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/ai/ncss.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (30, '기본 포함 품목', '지능형 안전기술', '고속도로 주행보조', '고속도로 본선 주행 시 전방 차량과의 거리, 차선 정보, 내비게이션 정보를 이용하여 차량 속도를 제어는 물론 차로 유지를 보조해줍니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/ai/hba.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (31, '기본 포함 품목', '지능형 안전기술', '차로 유지 보조', '전방 카메라를 이용하여 차선을 인식하고 차로의 중앙으로 주행할 수 있도록 조향을 보조합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/ai/lfa.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (32, '기본 포함 품목', '지능형 안전기술', '전방 충돌방지 보조(차량/보행자/자전', '전방 카메라와 전방 레이더의 신호를 종합적으로 판단하여 전방 차량, 보행자, 자전거 탑승자와 충돌 위험 상황이 감지될 경우 운전자에게 이를 경고하고, 필요 시 브레이크 작동을 도와줍니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/ai/fca.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (33, '기본 포함 품목', '지능형 안전기술', '차로 이찰방지 보조', '전방 카메라로 주행 차로를 실시간으로 감지하여 차량이 차로를 이탈하려 할 경우, 클러스터에 경고하고 스티어링 휠을 제어하여 안전하게 주행 차로를 유지하도록 보조합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/ai/lka.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (34, '기본 포함 품목', '지능형 안전기술', '운전자 주의 경고', '차량이 스스로 운전자의 주행 패턴과 차로 내 차량 위치 등을 종합적으로 분석하여 주의 운전이 필요하다고 판단되면, 팝업메시지와 경고음을 통해 운전자 휴식을 유도합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/ai/daw.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (35, '기본 포함 품목', '지능형 안전기술', '후측방 충돌 경고(주행)', '차로 변경을 위하여 방향지시등 스위치 조작 시, 후측방 충돌 위험이 감지되면 경고를 해줍니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/ai/bcw.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (36, '기본 포함 품목', '지능형 안전기술', '후측방 충돌방지 보조(전진 출차)', '평행 주차상태에서 전진 출차 중, 후측방 차량과 충돌 위험이 감지되면 자동으로 제동을 도와줍니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/ai/bca.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (37, '기본 포함 품목', '지능형 안전기술', '안전 하차 보조', '정차 후 탑승자가 차에서 내리려고 도어를 열 때, 후측방에서 접근하는 차량이 감지되면 경고를 해줍니다. 또한 전자식 차일드 락이 작동하여 문이 열리지 않도록 도와줍니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/ai/sea.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (38, '기본 포함 품목', '지능형 안전기술', '후방 교차 충돌방지 보조', '후진 출차 시 후방 교차 차량을 감지하여 운전자에게 경고하고 필요 시에는 브레이크 제어를 통해 후방 교차 충돌방지를 보조합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/ai/rcca.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (39, '기본 포함 품목', '안전', '10에어백 시스템(1열 어드밴스드/센', '1열 어드밴스드/센터 사이드, 운전석 무릎,1/2열 사이드, 전복 대응 커튼 에어백으로 탑승자를 보호합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/safe/10airbag.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (40, '기본 포함 품목', '안전', '유아용 시트 고정장치(2열 2개/3열', '영·유아용 시트를 간편하고 안전하게 장착할 수 있는 ISO 규격의 카시트를 고정할 수 있는 장치를 2열에 2개, 3열에 1개 총 3개를 적용하여 사고 시 카시트에 탑승한 유아를 보호합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/safe/isofix.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (41, '기본 포함 품목', '안전', '구동선회 제어기능', '커브구간에서 선회 가속시 구동력과 제동력을 제어해 언더스티어 현상을 억제하고 차량의 주행 민첩성과 주행 안정성을 향상시키는 토크벡터링 (Torque Vectoring) 시스템입니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/safe/atcc.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (42, '기본 포함 품목', '안전', '다중 충돌방지 자동 제동 시스템', '차량의 정면 또는 측면 충돌사고로 에어백 전개 시 차량에 적절한 자동 긴급 제동을 통해 2차 사고 및 다중 충돌을 경감시켜줍니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/safe/mcbs.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (43, '기본 포함 품목', '외관', '다크 크롬 라디에이터 그릴', null, null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/external/darkchromegrill.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (44, '기본 포함 품목', '외관', 'LED 주간주행등', '주간주행등(DRL)은 낮시간에도 자동으로 켜져 다른 사람들이 차량을 쉽게 볼 수 있도록 합니다. 특히 해질 무렵이나 해뜨기 직전에 차량의 접근을 쉽게 인식하도록 합니다. 차량 시동 후에 자동으로 켜집니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (45, '기본 포함 품목', '외관', 'LED 포지셔닝 램프', '안개, 야간과 같이 시야확보가 어려울 경우, 차의 존재 및 차폭을 인지할 수 있도록 하는 역할을 합니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (46, '기본 포함 품목', '외관', 'LED 방향지시등', '차량의 진행 방향을 주변 주행 차량 및 보행자에게 알리는 등화장치입니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (47, '기본 포함 품목', '외관', 'Full LED 헤드램프(프로젝션 타', '야간 운전시 전방에 빛을 비춰 운전자의 시야를 확보할 수 있도록 돕습니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (48, '기본 포함 품목', '외관', '아웃사이드 미러(열선, 전동접이, 전', '주행 중 후측방을 확인할 수 있도록 차량 좌우측에 부착된 거울입니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (49, '기본 포함 품목', '외관', '이중접합 차음유리(윈드실드, 1/2열', '두 겹의 유리로 이루어진 전방 유리의 사이에 소음을 흡수하는 전용 차음필름을 삽입하여 엔진 투과음 및 외부 소음이 실내에 유입되는 것을 감소시켜 탑승자의 운전 쾌적성을 향상시킵니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/external/laminatedwindow.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (50, '기본 포함 품목', '외관', '도어 포켓 라이팅(1열)', '스마트 키를 소지하고 차량에 접근하거나 리모컨 키를 이용하여 차량 도어를 오픈 할 때 아웃사이드 도어핸들 안쪽에 불빛을 비춰 야간에 차량의 문을 쉽게 열 수 있도록 배려한 편의 기능입니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/external/doorpocketlighting.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (51, '기본 포함 품목', '외관', '싱글 트윈팁 머플러', '차량 후면부 하단에 부착되는 배기장치로 차량 후면부와 조화감 있는 이미지로 디자인의 완성도를 높여줍니다. 머플러 부착방식에 따라 명칭이 상이합니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (52, '기본 포함 품목', '외관', 'LED 리어콤비램프', '헤드램프와 통일감 있는 콤비램프 디자인을 적용하였으며 보석 느낌의 인너렌즈를 적용하여 고급스러운 감성품질을 제공합니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (53, '기본 포함 품목', '외관', 'LED 보조제동등', '기본으로 제공하는 제동등과 별도로 장착되어 브레이크 작동 시 후방 주행 차량에게 제동을 알려 후방 추돌 상황을 방지하는 등화장치입니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (54, '기본 포함 품목', '외관', '후진가이드램프', '후진 시 점선 패턴의 가이드 조명 구현을 통해 주변 차량 및 보행자에게 후진 의도를 효과적으로 전달하여 사고를 예방합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/external/rearguidelamp.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (55, '기본 포함 품목', '외관', '리어 스포일러', '주행 시 차량 후면에 발생하는 공기의 와류 현상을 없애주는 장치로 차량의 공력 성능을 향상시킵니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/external/rearspoiler.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (56, '기본 포함 품목', '외관', '크롬 DLO 몰딩', '차량 도어의 몰딩 부분 소재를 크롬으로 적용하여 고급스러운 감성을 전달합니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (57, '기본 포함 품목', '외관', '루프랙', '차량 위에 짐을 실을 수 있도록 돕는 장치로 화물적재를 위한 보조기구 장착 시 차량 상태 및 안전을 고려하여 장착해야합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/external/roofrack.jpeg', null);
