INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (60, '기본 포함 품목', '외관', '발수 도어(1열)', '빗물 맺힘이나 서리, 성에 등을 막아주는 발수 적용 유리를 앞도어에 장착하여 운전자의 시계를 확보합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/external/frontdoorwindow_s.jpeg', null);
