INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (62, '기본 포함 품목', '내장', 'LED 실내등(맵램프, 룸램프, 선바', '차량 실내 전체를 비춰주는 룸램프를 LED로 적용하여 어두운 곳에서의 시인성을 향상시킵니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/internal/ledlamp.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (63, '기본 포함 품목', '내장', '가죽 스티어링 휠(열선 포함)', '시동 스위치가 「ON」 또는 엔진이 작동 중일때 스티어링 휠 히터 버튼을 누르면 표시등이 켜지면서 스티어링 휠이 따뜻해집니다. 스티어링 휠 히터 버튼을 누른 후 작동 조건에서 약 30분이 지나면 자동으로 작동을 멈춥니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/internal/leathersteeringwheel.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (64, '기본 포함 품목', '내장', '클러스터(12.3인치 컬러 LCD)', '컬러 LCD 클러스터(1,920x720)는 시인성이 높고 정보 파악이 용이하며, 주행모드별 차별화된 그래픽으로 즐거운 드라이빙 환경을 제공합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/internal/12.3cluster.jpg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (65, '기본 포함 품목', '내장', '인조가죽 감싸기', '-', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/internal/leatherwrapping.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (66, '기본 포함 품목', '내장', '도어 암레스트 리얼 스티치', '-', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/internal/doorarmrest.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (67, '기본 포함 품목', '시트', '1열 열선/통풍 시트', '시동이 걸린 상태에서 해당 좌석 히터 스위치를 누르면 강약조절 표시등이 켜져 사용 중임을 나타내고 해당 좌석이 따뜻해지는 열선기능과 시동이 걸린 상태에서 해당 좌석의 통풍 스위치를 누르면 표시등이 켜지면서 해당 좌석에 바람이 나오는 통풍기능이 적용되었습니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/sheet/frontheated.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (68, '기본 포함 품목', '시트', '2열 열선시트', '3단계로 온도 조절이 가능한 열선시트를 적용하여 쾌적한 승차감을 제공합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/sheet/2_heated.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (69, '기본 포함 품목', '시트', '2열 폴드 & 다이브/슬라이딩 & 리', '평면형태의 2열 공간을 제공하는 풀플랫 폴딩 2열시트로 차원이 다른 공간/거주성을 확보해 넉넉한 러기지 공간은 물론 프라이빗 스페이스로 활용할 수 있습니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/sheet/fold_dive.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (70, '기본 포함 품목', '시트', '3열 6:4분할 폴딩/리클라이닝 시트', '3열 시트 및 러기지 사이드에 적용된 전자식 스위치로 시트를 폴딩 및 리클라이닝하여 화물 적재 시 편리합니다. 또한 좌석 등받이 부분을 후방으로 눕힐 수 있는 리클라이닝 기능을 적용하여 성인 탑승 및 장거리 이동 시 거주 편의성을 높였습니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/sheet/64folding.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (71, '기본 포함 품목', '시트', '퀼팅 천연가죽 시트(블랙/쿨그레이)', '팰리세이드는 옵션에 따라 인조가죽, 가죽, 나파가죽, 퀼팅 나파가죽 시트를 선택할 수 있습니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (72, '기본 포함 품목', '시트', '운전석 전동시트(10way, 4way', '운전석의 시트 포지션을 조정하여 운전자의 체형에 맞는 편안한 자세를 유지할 수 있도록 돕는 기능입니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (73, '기본 포함 품목', '시트', '동승석 전동시트(8way, 워크인 디', '동승석의 시트 포지션을 조정하여 동승자의 체형에 맞는 편안한 자세를 유지할 수 있도록 돕는 기능입니다. 8방향(시트백 기울기, 시트 앞/뒤 이동, 쿠션부 앞/뒤 높이 조절)으로 조절이 기능하고 운전자 및 뒷좌석(2열) 승객이 동승석 시트 위치를 조절할 수 있는 워크인 디바이스를 적용하여 실내 공간 활용 편의성을 높였습니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/sheet/10_passengerseat.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (74, '기본 포함 품목', '편의', '버튼시동 & 스마트키', '일반 키와는 달리 휴대만으로 도어(트렁크 포함)를 잠그거나 열고, 엔진 시동을 걸 수 있는 장치입니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/smartkey.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (75, '기본 포함 품목', '편의', '전동식 파킹 브레이크(오토홀드 포함)', '버튼 조작만으로 파킹 브레이크 작동 또는 해제가 가능하며, 정차 시 자동으로 제동상태를 유지하는 오토 홀드 기능 적용으로 편의성을 높였습니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/epb.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (76, '기본 포함 품목', '편의', '패들 쉬프트', '운전 중 간단한 조작만으로도 스포티한 수동 변속모드를 지원합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/paddleshift.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (77, '기본 포함 품목', '편의', '3존 독립제어 풀오토 에어컨 (공기청', '원하는 온도를 설정하면 자동으로 설정된 온도로 풍량을 조절하여 쾌적한 실내를 완성합니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (78, '기본 포함 품목', '편의', '공기질 센서', '실내 공기질을 실시간으로 확인할 수 있어 공기 청정 모드 사용이나 환기를 유도합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/airsensor.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (79, '기본 포함 품목', '편의', '마이크로 에어 필터', '미세먼지의 실내 유입의 방지하기 위한 에어필터입니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (80, '기본 포함 품목', '편의', '자외선 차단 유리(윈드실드)', '차량 전방 유리에 자외선을 차단 기능을 적용하여 탑승객은 물론 차량 실내 내장재를 보호합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/uvprotectglass.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (81, '기본 포함 품목', '편의', '오토 디포그', '전면유리의 김서림을 감지해 스스로 전면 유리 및 앞좌석 유리의 김서림을 없애 운전시야를 확보합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/autodefog.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (82, '기본 포함 품목', '편의', '레인센서', '스티어링 휠 우측 레버의 와이퍼 조절장치 스위치를 AUTO로 위치하였을 때 주행 중 우천상황을 자동으로 감지하여 와이퍼가 작동 및 조작단계를 자동으로 조절하는 기능입니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/rainsensor.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (83, '기본 포함 품목', '편의', '운전석 공조 연동 자동 제어', '시동 후 공조 설정 온도와 실제 온도의 차이가 많이 나는 경우, 공조기 외 스티어링 휠 열선, 시트 열선/통풍을 자동으로 감지하여 통합 제어하는 기능입니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/driver_hvac.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (84, '기본 포함 품목', '편의', 'USB 충전기(1열 1개, 2열 2개', '차량용 충전기로서 USB 포트를 통해 각종 스마트폰 및 태블릿 기기 충전이 가능합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/usbcharger.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (85, '기본 포함 품목', '편의', '파워 아웃렛(1열 1개, 2열 1개,', '시동이 걸린 상태에서 실내에서 전기 제품(예 : 진공청소기, 소형냉장고, 게임기 등)을 사용할 수 있도록 돕습니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/poweroutlet.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (86, '기본 포함 품목', '편의', '세이프티 파워 윈도우(1/2열)', '유리창이 원터치 자동 닫힘 기능이 작동하여 닫히는 중에 일정한 힘에 의해 막히면 자동으로 닫힘을 멈추고, 일정한 높이만큼 다시 열리는 기능입니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/safetypowerwindow.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (87, '기본 포함 품목', '편의', '후방 모니터', '후진 주차 시 디스플레이 오디오 또는 내비게이션 화면에 후방 상황을 표시하여 편의성을 높였습니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (88, '기본 포함 품목', '편의', '확산형 루프 에어 밴트', '벤트 테두리에 홀(HOLE)을 적용하여 바람이 직접 분사가 아닌 간접(확산) 분사될 수 있도록 구현하여 조금 더 쾌적한 환경을 제공합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/roofairvent.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (89, '기본 포함 품목', '편의', '오토 라이트 컨트롤', '대시보드 중앙에 위치한 조도센서를 이용하여 외부 밝기에 따라 헤드램프를 자동으로 조절하는 장치입니다. 스티어링 휠 좌측에 위치한 조명 스위치를 「AUTO」 위치에 두면 작동합니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (90, '기본 포함 품목', '편의', '스마트키 원격시동', '차량 탑승 전 스마트키를 이용하여 원격으로 시동을 걸 수 있으며, 냉/난방 장치는 시동을 끄기 전 설정된 상태로 작동이 가능합니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (91, '기본 포함 품목', '편의', '전방/후방 주차 거리 경고', '범퍼에 내장된 초음파 센서로 장애물과의 거리를 감지하여 거리별로 차별화된 경고음을 울려 주차 편의성 및 안전성을 제공합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/pdw.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (92, '기본 포함 품목', '편의', '전동식 틸트 & 텔레스코픽 스티어링 ', '조절 스위치를 이용하여 스티어링 휠의 높낮이와 전/후 위치를 조절할 수 있습니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/autosteering.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (93, '기본 포함 품목', '편의', '스마트폰 무선충전', '중앙 콘솔에 휴대폰 무선 충전을 위한 시스템이 적용되어 있습니다. 또한 엔진에 시동을 끈 후 충전 패드에 휴대폰이 놓여있는 상태에서 운전석 또는 동승석 도어를 열면 게시판에 \''휴대폰이 무선 충전기에 있습니다\''라는 경고문 및 경고음(음성안내 적용 차량)으로 알려줍니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (94, '기본 포함 품목', '편의', '스마트 파워 테일게이트', '스위치 및 스마트키 버튼으로 테일게이트 개방이 가능하며, 설정을 통해 개폐 속도 조절 및 열림 높이 조절이 가능합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/powertailgate.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (95, '기본 포함 품목', '편의', '서라운드 뷰 모니터', '차량 앞/뒤/좌/우 360도 모든 상황을 AVN화면을 통해 볼 수 있는 장치로 고화질 카메라 및 디지털 영상 전송 방식을 적용하여 영상 경계선 없이 선명하고 깨끗한 화질을 제공합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/svm.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (96, '기본 포함 품목', '편의', '후측방 모니터', '방향지시등 스위치 조작과 연동해 차로 변경 시 기존 아웃사이드 미러 대비 더 넓은 후측방 영역을 클러스터에 표시하여 안전한 주행을 도와줍니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/convenience/bvm.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (97, '기본 포함 품목', '멀티미디어', '12.3인치 내비게이션(블루링크, 폰', '와이드하고 품격 있는 실내공간을 연출하고 고해상도 대화면으로 뛰어난 시인성과 최첨단 인포테인먼트 기능을 제공합니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (98, '기본 포함 품목', '멀티미디어', '하이패스', '유료 도로로 진출입시 통행료가 자동결제 되는 장치입니다. 하이패스 시스템은 시동이 걸려있는 상태에서만 작동합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/multimedia/hipass.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (99, '기본 포함 품목', '멀티미디어', '후석 대화모드', '운전자의 음성을 운전석 마이크를 통하여 뒷좌석 동승자와의 대화 편의성을 높였습니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/multimedia/reartalking.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (100, '기본 포함 품목', '멀티미디어', '후석 취침모드', '뒷좌석 동승자가 취침 시, 뒷좌석 스피커가 모두 음소거가 되고 운전석 스피커로만 음원이 출력되어, 뒷좌석을 조용한 공간으로 활용할 수 있습니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/multimedia/rearsleep.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (101, '기본 포함 품목', '멀티미디어', '일반 오디오 시스템(8스피커, 블루투', '차량 내부 공간의 음향 특성에 맞게 세심하게 조정된 8개의 스피커를 통하여 기본에 충실한 사운드를 감상할 수 있습니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/multimedia/audiosystem.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (102, '기본 포함 품목', '멀티미디어', '멀티미디어용 USB단자(1열 1개)', 'MP3, USB 저장 장치 또는 iPod 등 외부 음향 기기(또는 음악/동영상 등 미디어 파일 저장 장치)를 차량에 연결하여 차량 스피커를 통하여 음악을 듣거나 차량 오디오 또는 인포테인먼트 시스템으로 재생할 수 있는 편의 장치 입니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/multimedia/multiusb.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (103, '선택품목', '상세 품목', '컴포트 II - 후석 승객 알림', '[후석 승객 알림]초음파 센서를 통해 뒷좌석에 남아있는 승객의 움직임을 감지하여 운전자에게 경고함으로써 부주의에 의한 유아 또는 반려 동물 등의 방치 사고를 예방하는 신기술입니다.[메탈 리어범퍼스텝]러기지 룸 앞쪽 하단부를 메탈로 만들어 물건을 싣고 내릴 때나 사람이 올라갈 때 차체를 보호해줍니다.[메탈 도어스커프]자동차를 타고 내리는 도어의 문틈 하부를 보호하는 도어스커프 부분을 메탈로 만들어 차체를 보호하고 메탈 디자인으로 고급스러운 감성을 전달합니', 1090000, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/roa.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (104, '선택품목', '상세 품목', '컴포트 II - 메탈 리어범퍼스텝', '[메탈 리어범퍼스텝]러기지 룸 앞쪽 하단부를 메탈로 만들어 물건을 싣고 내릴 때나 사람이 올라갈 때 차체를 보호해줍니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/metalrearbumper.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (105, '선택품목', '상세 품목', '컴포트 II - 메탈 도어스커프', '[메탈 도어스커프]자동차를 타고 내리는 도어의 문틈 하부를 보호하는 도어스커프 부분을 메탈로 만들어 차체를 보호하고 메탈 디자인으로 고급스러운 감성을 전달합니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (106, '선택품목', '상세 품목', '컴포트 II - 3열 파워폴딩시트', '[3열 파워폴딩시트]러기지 사이드에 있는 버튼으로 3열 시트를 접었다 펼 수 있으며, 2열 시트도 조작할 수 있어 화물 적재시나 3열 이용시 사용자의 편의성을 높였습니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/powerfolding.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (107, '선택품목', '상세 품목', '컴포트 II - 3열 열선시트', '[3열 열선시트]시동이 걸린 상태에서 해당 좌석 히터 스위치를 누르면 강약조절 표시등이 켜져 사용 중임을 나타내고 해당 좌석이 따뜻해집니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/3_heated.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (108, '선택품목', '상세 품목', '컴포트 II - 헤드업 디스플레이', '[헤드업 디스플레이]주요 주행 정보를 전면 윈드실드에 표시하며, 밝기가 최적화되어 주간에도 시인성이 뛰어납니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/headupdisplay.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (109, '선택품목', '상세 품목', '주차보조 시스템 II - 후방 주차 ', '[후방 주차 충돌방지 보조]주차 또는 출차 시 저속 후진 중 후방카메라와 센서로 정후면에 위치한 보행자 및 장애물과의 충돌이 예상되면 운전자에게 경고하고 차량의 제동을 제어하여 충돌방지를 보조합니다.[원격 스마트 주차 보조]주차 보조 기능을 활성화 한 후 주차공간을 발견하게 되면 차량 내 안내에 따라 하차한 다음, 스마트키의 작동 버튼을 누르고만 있으면 차가 스스로 주차합니다. 직각주차 및 평행주차 모두 가능하며, 운전자 탑승 시에도 차량 내부의 작동 ', 690000, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/pca.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (110, '선택품목', '상세 품목', '주차보조 시스템 II - 원격 스마트', '[원격 스마트 주차 보조]주차 보조 기능을 활성화 한 후 주차공간을 발견하게 되면 차량 내 안내에 따라 하차한 다음, 스마트키의 작동 버튼을 누르고만 있으면 차가 스스로 주차합니다. 직각주차 및 평행주차 모두 가능하며, 운전자 탑승 시에도 차량 내부의 작동 버튼을 누르고 있으면 자동 주차 보조를 지원합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/rspa.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (111, '선택품목', '상세 품목', '2열 통풍 시트', '시동이 걸린 상태에서 해당 좌석의 통풍 스위치를 누르면 표시등이 켜지면서 해당 좌석에 바람이 나오는 편의장치입니다.', 400000, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/2_cooling.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (112, '선택품목', '상세 품목', '듀얼 와이드 선루프', '후석에 고정 글라스를 적용한 듀얼 파노라믹 선루프로 2/3열의 탑승객에게도 넓은 개방감을 선사합니다.', 890000, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/sun_roof.jpg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (113, '선택품목', '상세 품목', '빌트인 캠(보조배터리 포함)', '빌트인 적용된 영상기록장치로, 내비게이션 화면을 통해 영상 확인 및 앱 연동을 통해 영상 확인 및 SNS 공유가 가능합니다.', 690000, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/cam.jpg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (114, '선택품목', '상세 품목', '현대스마트센스 I - 전방 추돌방지 ', '[전방 충돌방지 보조(교차 차량/추월시 대향차/측방 접근차)]선행 차량이 갑자기 속도를 줄이거나, 앞에 정지 차량 혹은 보행자가 나타나는 등 전방 충돌 위험이 감지되면 경고를 해줍니다. 경고 후에도 충돌 위험이 높아지면 자동으로 제동을 도와줍니다. 주행 중 전방의 자전거 탑승자 및 교차로에서 좌회전 시 맞은편에서 다가오는 차량과 충돌 위험이 있다면 자동으로 제동을 도와줍니다.[내비게이션 기반 스마트 크루즈 컨트롤(진출입로)]스마트 크루즈 작동 중 고속도', 790000, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/smart_front_collision.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (115, '선택품목', '상세 품목', '현대스마트센스 I - 내비게이션 기반', '스마트 크루즈 작동 중 고속도로/도시고속도로/자동차전용 도로 내 고속도로 진출입로 주행 시 차로를 판단하여 사전감속 또는 최적 속도에 맞추어 감속을 진행합니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/smar_ncss.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (116, '선택품목', '상세 품목', '현대스마트센스 I - 고속도로 주행 ', '[고속도로 주행 보조 2] 고속도로 / 자동차 전용도로에서 앞차와의 거리와 설정 속도를 유지하며 주행할 뿐 아니라, 곡선로에서도 차로의 중앙을 유지하며 주행할 수 있도록 도와줍니다. 일정 속도 이상으로 주행 시, 스티어링 휠을 잡은 상태에서 방향지시등 스위치를 변경하고자 하는 차로 방향으로 움직이면 자동으로 차로를 변경해 줍니다.', null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/selected/smart_highway.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (117, '선택품목', 'H Genuine Accessorie', '듀얼 머플러 패키지', '[내비게이션 기반 스마트 크루즈 컨트롤(진출입로)]차량 후면부에 스포티함을 한층 강화시켜주는 듀얼 머플러와 디자인 리어 스키드 상품입니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (118, '선택품목', 'H Genuine Accessorie', '빌트인 공기청정기', 'H13 등급 및 활성탄+제올라이트 복합소재 필터를 적용하여 초미세먼지뿐 아니라 실내 유해가스(VOCs) 정화 기능이 우수한 상품입니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (119, '선택품목', 'H Genuine Accessorie', '사이드스텝', '뛰어난 일체감을 통해 사이드 디자인을 개선시켜 주며, 미끄럼 방지 패턴이 적용되어 어린이 및 노약자의 승하차 시 안정성을 높여주는 상품입니다.', 350000, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/H_Genuine_Accessories/sidestep.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (120, '선택품목', 'H Genuine Accessorie', '적외선 무릎워머', '스티어링 컬럼 하단에 설치된 복사 열선이 운전자의 허벅지와 무릎에 복사열을 전달하여 저온에서 히터 바람 없이도 냉감을 줄여 보다 쾌적한 환경을 제공해주는 상품입니다.', 300000, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/H_Genuine_Accessories/kneewarmer.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (121, '선택품목', 'H Genuine Accessorie', '차량 보호 필름', '카 라이프중 손상되기 쉬운 프론트/리어 범프등 7부위에 우레탄 계열의 투명한 보호 필름을 부착하여 스톤칩으로 인한 페인트 손상 및 생활 스크래치를 예방하여 깨끗한 바디를 유지하는데 도움을 줍니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (122, '선택품목', 'H Genuine Accessorie', '프로텍션 매트 패키지 I - 러기지 ', '차량 설계 데이터를 반영한 3D 형상 디자인으로 차량에 딱 맞는 완벽한 일체감과 실내 고급감을 제공합니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (123, '선택품목', 'H Genuine Accessorie', '프로텍션 매트 패키지 I - 플로어매', null, null, 'https://topcariving.s3.ap-northeast-2.amazonaws.com/H_Genuine_Accessories/floormat.jpeg', null);
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (124, '선택품목', 'N performance', '20인치 다크 스퍼터링 휠', '스포티한 디자인 감성을 원하는 고객을 위한 N 퍼포먼스 파츠 휠입니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (125, '선택품목', 'N performance', '20인치 블랙톤 전면 가공 휠', '알콘 브레이크 시스템 장착을 위한 전용휠이며 다이아몬트 커팅 피니쉬로 플래그십 SUV에 어울리는 고급스러운 디자인 감성을 제공합니다.
INSERT INTO CAR_OPTION (car_option_id, category, category_detail, option_name, option_detail, price, photo_url, parent_option_id) VALUES (126, '선택품목', 'N performance', '알콘(alcon) 단조 브레이크 & ', '6피스톤 단조 캘리퍼 : 단조 소재를 적용하여 강성을 증대하였으며(기존: 주조) 피스톤 직경 증대로 제동 응답성이 향상되었습니다.
